styles:
:host {
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  -ms-flex-wrap: wrap;
      flex-wrap: wrap;
  -webkit-box-align: start;
      -ms-flex-align: start;
          align-items: flex-start;
  border: 1px solid #ddd;
  cursor: text;
  -webkit-box-sizing: border-box;
          box-sizing: border-box;
  font-family: Sans-Serif;
}

:host .ng-invalid {
  background-color: rgba(169, 68, 66, .2);
}

* {
  outline: none;
}

.tags-container {
  border: none;
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-orient: horizontal;
  -webkit-box-direction: normal;
      -ms-flex-direction: row;
          flex-direction: row;
  -ms-flex-wrap: wrap;
      flex-wrap: wrap;
  -webkit-box-align: start;
      -ms-flex-align: start;
          align-items: flex-start;
  -webkit-box-flex: 1;
      -ms-flex: 1;
          flex: 1;
  padding: 0 10px;
}

.tag {
  background-color: #eee;
  border-radius: 4px;
  color: #333;
  cursor: default;
  display: inline-block;
  -ms-flex-negative: 0;
      flex-shrink: 0;
  font-size: 14px;
  line-height: 22px;
  margin: 2px 5px 2px 0;
  padding: 0 4px;
}

.tag button {
  cursor: pointer;
  border: none;
  background: transparent;
  color: #aaa;
  font-size: 14px;
  height: 20px;
  line-height: 20px;
  padding: 0px;
}

.tag button:hover {
  color: #555;
}

input {
  height: 22px;
  line-height: 22px;
  font-size: 14px;
  -webkit-box-flex: 1;
      -ms-flex: 1 0 50px;
          flex: 1 0 50px;
  border: none;
  background: transparent;
  margin: 2px 6px !important;
}

.blink {
  -webkit-animation: pulse 2s ease-out 1 alternate;
  animation:         pulse 2s ease-out 1 alternate; 
}
@-webkit-keyframes pulse {
  0% {
    background: rgb(43, 179, 122);
  }
  10% {
    background: rgb(43, 179, 122);
  }
  50% {
    background-color: #eee;
  }
}
@keyframes pulse {
  0% {
    background: rgb(43, 179, 122);
  }
  10% {
    background: rgb(43, 179, 122);
  }
  50% {
    background-color: #eee;
  }
}
